import React, { useState } from 'react';
import '../styles/components.css';
import PlaybackControls from './PlaybackControls';

const initialSections = [
  {
    name: 'forYou',
    displayName: 'For You',
    visible: true,
    items: [
      'Shine On You Crazy Diamond',
      'Comfortably Numb',
      'Wish You Were Here',
      'Time',
      'Money',
      'Another Brick in the Wall, Part 2',
      'Hey You',
      'Us and Them'
    ]
  },
  {
    name: 'browse',
    displayName: 'Browse',
    visible: true,
    items: [
      'The Beatles',
      'Led Zeppelin',
      'The Rolling Stones',
      'Pink Floyd',
      'Queen',
      'The Who',
      'The Kinks',
      'Genesis'
    ]
  },
  {
    name: 'radio',
    displayName: 'Radio',
    visible: true,
    items: [
      'British Invasion Essentials',
      'Psychedelic Rock Journey',
      'Legends of Classic Rock',
      'The Vinyl Experience',
      'British Rock Unplugged',
      'Progressive Rock Essentials',
      'Underground Classics',
      'Retro Road Trip'
    ]
  },
  {
    name: 'todaysHit',
    displayName: "Today's Hit",
    visible: true,
    items: [
      'Blinding Lights - The Weeknd',
      'Levitating - Dua Lipa',
      'Peaches - Justin Bieber',
      'Stay - The Kid LAROI, Justin Bieber',
      'Montero (Call Me By Your Name) - Lil Nas X',
      'Save Your Tears - The Weeknd',
      'Good 4 U - Olivia Rodrigo',
      'Deja Vu - Olivia Rodrigo'
    ]
  }
];

const Home = () => {
  const [sections, setSections] = useState(initialSections);
  const [editingSection, setEditingSection] = useState(null);
  const [newTitle, setNewTitle] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [searchQueries, setSearchQueries] = useState({});
  const [itemTags, setItemTags] = useState({});

  const toggleVisibility = (name) => {
    setSections(prevSections =>
      prevSections.map(section =>
        section.name === name ? { ...section, visible: !section.visible } : section
      )
    );
  };

  const moveSection = (name, direction) => {
    const index = sections.findIndex(section => section.name === name);
    if (index < 0) return;

    const newSections = [...sections];
    const targetIndex = direction === 'up' ? index - 1 : index + 1;

    if (targetIndex >= 0 && targetIndex < newSections.length) {
      const [movedSection] = newSections.splice(index, 1);
      newSections.splice(targetIndex, 0, movedSection);
      setSections(newSections);
    }
  };

  const startEditing = (section) => {
    setEditingSection(section.name);
    setNewTitle(section.displayName);
    setNewDescription(section.description || "");
  };

  const saveEdits = (name) => {
    setSections(prevSections =>
      prevSections.map(section =>
        section.name === name ? { ...section, displayName: newTitle, description: newDescription } : section
      )
    );
    setEditingSection(null);
    setNewTitle("");
    setNewDescription("");
  };

  const handleSearch = (sectionName, query) => {
    setSearchQueries(prevQueries => ({
      ...prevQueries,
      [sectionName]: query.toLowerCase(),
    }));
  };

  const addTag = (item, tag) => {
    if (!tag) return;

    setItemTags(prevTags => ({
      ...prevTags,
      [item]: prevTags[item] ? [...prevTags[item], tag] : [tag]
    }));
  };

  return (
    <div>
      <h1>Home</h1>
      <div className="sections">
        {sections.map(section => (
          <div key={section.name} className="section">
            {editingSection === section.name ? (
              <div>
                <input
                  type="text"
                  value={newTitle}
                  onChange={(e) => setNewTitle(e.target.value)}
                  placeholder="New title"
                />
                <textarea
                  value={newDescription}
                  onChange={(e) => setNewDescription(e.target.value)}
                  placeholder="Description"
                />
                <button onClick={() => saveEdits(section.name)}>Save</button>
              </div>
            ) : (
              <div>
                <h2>{section.displayName}</h2>
                <p>{section.description}</p>
                {section.visible && (
                  <div>
                    <input
                      type="text"
                      placeholder="Search items..."
                      onChange={(e) => handleSearch(section.name, e.target.value)}
                    />
                    <ul>
                      {section.items
                        .filter(item =>
                          searchQueries[section.name]
                            ? item.toLowerCase().includes(searchQueries[section.name])
                            : true
                        )
                        .map((item, index) => (
                          <li key={index}>
                            {item}
                            <div className="tags">
                              {itemTags[item]?.map((tag, tagIndex) => (
                                <span key={tagIndex} className="tag">{tag}</span>
                              ))}
                            </div>
                            <input
                              type="text"
                              placeholder="Add a tag"
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  addTag(item, e.target.value);
                                  e.target.value = ''; // Clear input after adding tag
                                }
                              }}
                            />
                          </li>
                        ))}
                    </ul>
                  </div>
                )}
                <button onClick={() => toggleVisibility(section.name)}>
                  {section.visible ? 'Hide' : 'Show'}
                </button>
                <button onClick={() => moveSection(section.name, 'up')}>Move Up</button>
                <button onClick={() => moveSection(section.name, 'down')}>Move Down</button>
                <button onClick={() => startEditing(section)}>Edit</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
