import React, { useState, useEffect } from 'react';
import '../styles/components.css';

const ItemDetailsModal = ({ item, onClose, onSaveAlias }) => {
  const [alias, setAlias] = useState('');

  useEffect(() => {
    if (item) {
      setAlias(item.alias || '');
    }
  }, [item]);

  const handleSaveAlias = () => {
    if (item) {
      onSaveAlias(item.name, alias);
      onClose();
    }
  };

  // This function stops the overlay click from closing the modal when clicking inside the modal content
  const handleModalContentClick = (e) => {
    e.stopPropagation();
  };

  if (!item) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>  {/* Clicking overlay will close modal */}
      <div className="modal-content" onClick={handleModalContentClick}>
        <button className="modal-close" onClick={onClose}>X</button>
        <h2>{item.name}</h2>
        <p>Artist: {item.artist}</p>

        <label>
          Alias:
          <input
            type="text"
            value={alias}
            onChange={(e) => setAlias(e.target.value)}
            placeholder="Set an alias"
          />
        </label>

        <button onClick={handleSaveAlias}>Save Alias</button>
      </div>
    </div>
  );
};

export default ItemDetailsModal;
