import React, { useState } from 'react';
import '../styles/components.css';
import PlaybackControls from './PlaybackControls';
import ItemDetailsModal from './ItemDetailsModal';

const Home = ({ libraryItems, addItem }) => {
  const [sections, setSections] = useState(initialSections);
  const [selectedItem, setSelectedItem] = useState(null);
  const [newItem, setNewItem] = useState({ name: '', itemType: 'song', alias: '', artist: '' });

  const handleSaveAlias = (itemName, newAlias) => {
    setSections(prevSections =>
      prevSections.map(section => ({
        ...section,
        items: section.items.map(item =>
          item.name === itemName ? { ...item, alias: newAlias } : item
        )
      }))
    );
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const closeModal = () => {
    setSelectedItem(null);
  };

  const handleAddNewItem = () => {
    if (newItem.name.trim() === '') return;

    addItem(newItem);
    setNewItem({ name: '', itemType: 'song', alias: '', artist: '' }); // Reset form
  };

  return (
    <div>
      <h1>Home</h1>

      <PlaybackControls items={sections[0].items} />

      {/* New Item Form */}
      <div className="add-item-form">
        <h3>Add a New Item</h3>
        <input
          type="text"
          placeholder="Item Name"
          value={newItem.name}
          onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Artist"
          value={newItem.artist}
          onChange={(e) => setNewItem({ ...newItem, artist: e.target.value })}
        />
        <select
          value={newItem.itemType}
          onChange={(e) => setNewItem({ ...newItem, itemType: e.target.value })}
        >
          <option value="song">Song</option>
          <option value="artist">Artist</option>
          <option value="playlist">Playlist</option>
        </select>
        <button onClick={handleAddNewItem}>Add Item</button>
      </div>

      <div className="sections">
        {sections.map((section, index) => (
          <div key={section.name} className="section">
            <h2>
              {section.displayName}
              <button onClick={() => toggleVisibility(section.name)} style={{ marginLeft: '10px' }}>
                {section.visible ? 'Fold' : 'Unfold'}
              </button>
              <button onClick={() => moveSection(index, 'up')} disabled={index === 0}>
                ↑
              </button>
              <button onClick={() => moveSection(index, 'down')} disabled={index === sections.length - 1}>
                ↓
              </button>
            </h2>
            {section.visible && (
              <ul>
                {section.items.map((item, itemIndex) => (
                  <li key={itemIndex}>
                    <span
                      onClick={() => handleItemClick(item)}
                      style={{ cursor: 'pointer', textDecoration: 'underline' }}
                    >
                      {item.alias || item.name}
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        ))}
      </div>

      <ItemDetailsModal item={selectedItem} onClose={closeModal} onSaveAlias={handleSaveAlias} />
    </div>
  );
};

export default Home;
