import React, { useState } from 'react';
import ItemDetailsModal from '../components/ItemDetailsModal';
import '../styles/components.css';

const LibraryPage = () => {
  const [favoriteSongs, setFavoriteSongs] = useState([
    { name: 'Shine On You Crazy Diamond', artist: 'Pink Floyd', alias: '' },
    { name: 'Imagine', artist: 'John Lennon', alias: '' },
    { name: 'Bohemian Rhapsody', artist: 'Queen', alias: '' },
  ]);

  const [selectedItem, setSelectedItem] = useState(null);

  // Define handleSaveAlias and update favoriteSongs
  const handleSaveAlias = (itemName, newAlias) => {
    setFavoriteSongs((prevSongs) =>
      prevSongs.map((song) =>
        song.name === itemName ? { ...song, alias: newAlias } : song
      )
    );
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const closeModal = () => {
    setSelectedItem(null);
  };

  return (
    <div className="library-page">
      <h1>Your Library</h1>

      <div className="library-section">
        <h2>Favorite Songs</h2>
        <ul>
          {favoriteSongs.map((song, index) => (
            <li key={index} onClick={() => handleItemClick(song)}>
              {song.alias || song.name} by {song.artist}
            </li>
          ))}
        </ul>
      </div>

      {selectedItem && (
        <ItemDetailsModal
          item={selectedItem}
          onClose={closeModal}
          onSaveAlias={handleSaveAlias} // Ensure this is passed correctly
        />
      )}
    </div>
  );
};

export default LibraryPage;
